try
{
	//Get GAO token
	var sm = new sn_ws.RESTMessageV2("x_avepo_cloud_gove.Logon", "Post");
	
	sm.setStringParameter('data_center_url', gs.getProperty('x_avepo_cloud_gove.data_center_url'));
	var logonInfo = {"userName": gs.getProperty("x_avepo_cloud_gove.gao_user_name"), "passWord": gs.getProperty("x_avepo_cloud_gove.gao_user_password")};
	
	sm.setRequestBody(global.JSON.stringify(logonInfo));
	var response = sm.execute();
	var token = response.getBody();
	workflow.info(token.replace(/\"/g,''));
		
	
	var requestInfo = {};
	requestInfo.ServiceId = getServiceId(current.getValue('service'));
	requestInfo.Type = 121;
    var requester = current.getValue('sys_created_by');
	requestInfo.Requester = requester.indexOf("@")!=-1 ? requester : "";
	//assignment
	requestInfo.RequestSummary = current.getValue('request_summary');
	requestInfo.NotesToApprover = current.getValue('description');
	requestInfo.Department = current.getValue('department');
	
	requestInfo.GroupId = current.getValue('group_id');
	requestInfo.GroupEmail = current.getValue('group_id');
	if(current.getValue('group_name'))
	{
		requestInfo.GroupName = current.getValue('group_name');
	}
	if(current.getValue('group_description'))
	{
		requestInfo.GroupDescription = current.getValue('group_description');
	}
	if(current.getValue('subscribe'))
	{
		requestInfo.Subscribe = current.getValue('subscribe') == '1';
	}
	if(current.getValue('outside_senders'))
	{
		requestInfo.OutsideSender = current.getValue('outside_senders') == '1';
	}
	if(current.getValue('teams'))
	{
		requestInfo.EnableChangeTeamCollaboration = current.getValue('teams') == '1';
	}
	if(current.getValue('classification'))
	{
		requestInfo.Classification = current.getValue('classification');
	}
	if(current.getValue('primary_contact'))
	{
		requestInfo.PrimaryContact = getUserName(current.getValue('primary_contact'));
	}
	if(current.getValue('secondary_contact'))
	{
		requestInfo.SecondaryContact = getUserName(current.getValue('secondary_contact'));
	}
	if(current.getValue('owners') || current.getValue('removed_owners'))
	{
		var list = [];
		if(current.getValue('owners'))
		{
			var owners = getUserName(current.getValue('owners')).split(';');
			for(var index in owners)
			{
				var item = {};
				item.Name = owners[index];
				item.Status = {};
			    item.Status.Value = 1;
				list.push(item);
			}
		}
		if(current.getValue('removed_owners'))
		{
			var removed_owners = getUserName(current.getValue('removed_owners')).split(';');
			for(var index in removed_owners)
			{
				var item = {};
				item.Name = removed_owners[index];
				item.Status = {};
			    item.Status.Value = 2;
				list.push(item);
			}
		}
		requestInfo.ChangedOwners = global.JSON.stringify(list);
	}
	if(current.getValue('members') || current.getValue('removed_members'))
	{
		var list = [];
		if(current.getValue('members'))
		{
			var members = getUserName(current.getValue('members')).split(';');
			for(var index in members)
			{
				var item = {};
				item.Name = members[index];
				item.Status = {};
			    item.Status.Value = 1;
				list.push(item);
			}
		}
		if(current.getValue('removed_members'))
		{
			var removed_members = getUserName(current.getValue('removed_members')).split(';');
			for(var index in removed_members)
			{
				var item = {};
				item.Name = removed_members[index];
				item.Status = {};
			    item.Status.Value = 2;
				list.push(item);
			}
		}
		requestInfo.ChangedMembers = global.JSON.stringify(list);
	}
	if(current.getValue('group_metadata'))
	{
		var metadataList = [];
		var group_metadata = global.JSON.parse(current.getValue('group_metadata'));
		for(var item in group_metadata)
		{
			var metadata = {};
			metadata.Name = item;
			metadata.Value = group_metadata[item];
			metadata.Action = 2;
			metadataList.push(metadata);
		}
		requestInfo.ChangeMetadatas = global.JSON.stringify(metadataList);
	}
	
	if(current.getValue('metadata'))
	{
		var metadatas = global.JSON.parse(current.getValue('metadata'));
		var metadataList = [];
		for(var item in metadatas)
		{
			var metadata = {};
			metadata.Name = item;
			metadata.Value = metadatas[item];
			metadata.FieldType = 0;
			metadataList.push(metadata);
			workflow.info("metadata: " + global.JSON.stringify(metadata));
		}
		requestInfo.MetadataList = metadataList;
	}
	workflow.info(global.JSON.stringify(requestInfo));
	
	//Save request
	sm = new sn_ws.RESTMessageV2('x_avepo_cloud_gove.Save and Submit', 'Post');
	sm.setStringParameter('data_center_url', gs.getProperty('x_avepo_cloud_gove.data_center_url'));
	sm.setStringParameter("access_token", token.replace(/\"/g,''));
	sm.setRequestBody(global.JSON.stringify(requestInfo));
	response = sm.execute();
	var requestId = response.getBody().replace(/\"/g,'');
	var reg = new RegExp("^[0-9a-z]{8}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{12}$");
	if(requestId.match(reg) != null)
	{
		current.setValue('request_id', requestId);
		current.setValue('status', 'Successful');
		workflow.info('Successfully submitted the request via Cloud Governance API. Request Id: ' + requestId);
	}
	else
	{
		current.setValue('status', 'Failed');
		workflow.error('An error occurred when submitting the request via Cloud Governance API. Details: ' + requestId);
	}
}
catch(ex)
{
	current.setValue('status', 'Failed');
	workflow.error(ex);
}
function getUserName(id)
{
	if(!id)
	{
		return '';
	}
	var list = id.split(',');
	var gr = new GlideRecord("sys_user");
	var query = gr.addQuery('sys_id',list[0]); 
	if(list.length > 1)
	{
		for(var i = 1; i < list.length; i++)
		{
			query.addOrCondition('sys_id',list[i]); 
		}
	}
	gr.query();
	var result = '';
	while(gr.next()){ 
		result += gr.user_name + ";";
	}
	return result.replace(/(;*$)/g,"");
}
function getServiceId(id)
{
	var gr = new GlideRecordSecure("x_avepo_cloud_gove_service");
		gr.addQuery('sys_id', id);
		gr.query();
		var result = '';
		while(gr.next()){ 
			result = gr.service_id.toString();
			break;
		}
		return result;
}

