sys_variable_value.value:
// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
answer = [];
if(current.getValue('approvers'))
{
	var approvers = current.getValue('approvers');
	workflow.info('user: ' + approvers);
    answer.push(approvers);
}

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
answer = [];
if(current.getValue('approvers'))
{
	var approvers = current.getValue('approvers');
	workflow.info('user: ' + approvers);
    answer.push(approvers);
}

try
{
	//Get GAO token
	var sm = new sn_ws.RESTMessageV2("x_avepo_cloud_gove.Logon", "Post");
	
	sm.setStringParameter('data_center_url', gs.getProperty('x_avepo_cloud_gove.data_center_url'));
	var logonInfo = {"userName": gs.getProperty("x_avepo_cloud_gove.gao_user_name"), "passWord": gs.getProperty("x_avepo_cloud_gove.gao_user_password")};
	
	sm.setRequestBody(global.JSON.stringify(logonInfo));
	var response = sm.execute();
	var token = response.getBody();
	workflow.info(token.replace(/\"/g,''));
	var requestInfo = {};
		
	//assignment
	requestInfo.ServiceId = getServiceId(current.getValue('service'));
	requestInfo.Type = 1;
	var requester = current.getValue('sys_created_by');
	requestInfo.Requester = requester.indexOf("@")!=-1 ? requester : "";
	requestInfo.RequestSummary = current.getValue('request_summary');
	requestInfo.NotesToApprover = current.getValue('description');
	requestInfo.Department = current.getValue('department');
	requestInfo.SiteTitle = current.getValue('site_title');
	requestInfo.SiteDescription = current.getValue('site_description');
	
	requestInfo.Url = {};
	requestInfo.Url.Prefix = current.getValue('sc_url');
	requestInfo.Url.ManagedPath = current.getValue('managed_paths');
	requestInfo.Url.Url = current.getValue('url_name');
	if(current.getValue('policy'))
	{
		requestInfo.PolicyId = current.getValue('policy');
	}
	if(current.getValue('template'))
	{
		requestInfo.SiteTemplate = current.getValue('template');
	}
	if(current.getValue('language'))
	{
		requestInfo.Language = current.getValue('language');
	}
	if(current.getValue('time_zone'))
	{
		requestInfo.timeZone = current.getValue('time_zone');
	}
	if(current.getValue('dpm'))
	{
		requestInfo.SelectedDeploymentManagerPlanName = current.getValue('dpm');
	}
	if(current.getValue('primary_admin'))
	{
		requestInfo.PrimaryAdministrator = getUserName(current.getValue('primary_admin'));
	}
	if(current.getValue('additional_admin'))
	{
		requestInfo.AdditionalAdministrators = getUserName(current.getValue('additional_admin'));
	}
	if(current.getValue('sc_primary_contact'))
	{
		requestInfo.PrimaryContact = getUserName(current.getValue('sc_primary_contact'));
	}
	if(current.getValue('sc_secondary_contact'))
	{
		requestInfo.SecondaryContact = getUserName(current.getValue('sc_secondary_contact'));	
	}
	if(current.getValue('owner_group') || current.getValue('member_group') || current.getValue('visitor_group'))
	{
		var securityGroups = {};
		if(current.getValue('owner_group'))
		{
			securityGroups['owners'] = getUserName(current.getValue('owner_group'));
		}
		if(current.getValue('member_group'))
		{
			workflow.info('members: '+current.getValue('member_group'));
			securityGroups['members'] = getUserName(current.getValue('member_group'));
		}
		if(current.getValue('visitor_group'))
		{
			securityGroups['visitors'] = getUserName(current.getValue('visitor_group'));
		}
		requestInfo.SecurityGroups = global.JSON.stringify(securityGroups);
	}
	if(current.getValue('lease_period'))
	{
		requestInfo.LeasePeriod = current.getValue('lease_period');
		requestInfo.LeasePeriodType = current.getValue('lease_unit');
		if(current.getValue('lease_warning'))
		{
			requestInfo.LeaseWarning = current.getValue('lease_warning');
			requestInfo.WarningType = current.getValue('warning_unit');
		}
	}
	if(current.getValue('metadata'))
	{
		var metadatas = global.JSON.parse(current.getValue('metadata'));
		var metadataList = [];
		for(var item in metadatas)
		{
			var metadata = {};
			metadata.Name = item;
			metadata.Value = metadatas[item];
			metadata.FieldType = 0;
			metadataList.push(metadata);
			workflow.info("metadata: " + global.JSON.stringify(metadata));
		}
		requestInfo.MetadataList = metadataList;
	}
	workflow.info(global.JSON.stringify(requestInfo));
	
	//Save request
	sm = new sn_ws.RESTMessageV2('x_avepo_cloud_gove.Save and Submit', 'Post');
	sm.setStringParameter('data_center_url', gs.getProperty('x_avepo_cloud_gove.data_center_url'));
	sm.setStringParameter("access_token", token.replace(/\"/g,''));
	sm.setRequestBody(global.JSON.stringify(requestInfo));
	response = sm.execute();
	var requestId = response.getBody().replace(/\"/g,'');
	var reg = new RegExp("^[0-9a-z]{8}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{12}$");
	if(requestId.match(reg) != null)
	{
		current.setValue('request_id', requestId);
		current.setValue('status', 'Successful');
		workflow.info('Successfully submitted the request via Cloud Governance API. Request Id: ' + requestId);
	}
	else
	{
		current.setValue('status', 'Failed');
		workflow.error('An error occurred when submitting the request via Cloud Governance API. Details: ' + requestId);
	}

}
catch(ex)
{
	current.setValue('status', 'Failed');
	workflow.error(ex);
}

function getUserName(id)
{
	if(!id)
	{
		return '';
	}
	var list = id.split(',');
	var gr = new GlideRecord("sys_user");
	var query = gr.addQuery('sys_id',list[0]); 
	if(list.length > 1)
	{
		for(var i = 1; i < list.length; i++)
		{
			query.addOrCondition('sys_id',list[i]); 
		}
	}
	gr.query();
	var result = '';
	while(gr.next()){ 
		result += gr.user_name + ";";
	}
	return result.replace(/(;*$)/g,"");
}
function getServiceId(id)
{
	var gr = new GlideRecordSecure("x_avepo_cloud_gove_service");
		gr.addQuery('sys_id', id);
		gr.query();
		var result = '';
		while(gr.next()){ 
			result = gr.service_id.toString();
			break;
		}
		return result;
}

